int main(int argc, char *argv[])
{
    BUILDER_ARGS    bargs = { 0 };
    char            *_;

    if (argc < 2)
        kill("[*] Usage: %s <DllFileToConvert> <optional: offset to call in hex>\n", argv[0]);

    _strncpy(bargs.filepath, argv[1], sizeof(bargs.filepath) - 1);

    if (argc > 2) {
        bargs.opt |= SRDI_CALLOFFSET;
        bargs.offset = strtol(argv[2], &_, 16);
    }
    else
        /*
            * TODO: Fix to call default entrypoint if offset not provided
        */
        kill("[*] Only offset option supported now!(not optional lol)");

    build_shellcode(&bargs);

    return 0;
}

