#include <Windows.h>
#include <stdio.h>

#include "srdi.h"


int main(int argc, char *argv[])
{
    BUILDER_ARGS    bargs = { 0 };
    char            *_;

    if (argc < 2)
        kill("[*] Usage: %s <DllFileToConvert> <optional(not): offset to call in hex> <optional: args>...(max 4 only numbers in hex!)\n", argv[0]);

    _strncpy(bargs.filepath, argv[1], sizeof(bargs.filepath) - 1);

    if (argc > 2) {
        bargs.opt |= SRDI_CALLOFFSET;
        bargs.offset = strtol(argv[2], &_, 16);
    }
    else
        /*
            * TODO: Fix to idk function name maybe
        */
        kill("[*] Only offset option supported now!(not optional lol)");

    if (argc > 3)
        for (int i = 0; i < (argc - 3); i++)
            bargs.passargs[i] = strtol(argv[i + 3], &_, 16);

    bargs.argc = argc - 3;

    build_shellcode(&bargs);

    return 0;
}

