    /*
        * push rbp
        * mov rbp, rsp
        * sub sp, 0x600
        * call 0xe ; call to get current location in memory
        * 0xe: pop r14
        * add r14, 0x7d1 ; PE start
        * call 0x4b ; skip over offset execution part
    */
    unsigned char       get_base[] = { 0x55, 0x48, 0x89, 0xE5, 0x66, 0x81, 0xEC, 0x00, 0x06, 0xE8, 0x00, 0x00, 0x00, 0x00, 0x47, 0x5E, 0x49, 0x81, 0xC6, 0xd1, 0x07, 0x00, 0x00, 0xE8, 0x2f, 0x00, 0x00, 0x00 };
    
    /*
        * mov rbx, 0x0 ; dummy offset, will patch later
        * add rax, rbx
        * mov rcx, 0x0 ; dummy args, will patch
        * mov rdx, 0x0
        * mov r8, 0x0
        * mov r9, 0x0
        * call rax ; call offset
        * add sp,0x600 ; return cleanly
        * leave
        * ret
    */
    unsigned char       call_offset[] = { 0x48, 0xC7, 0xC3, 0x00, 0x00, 0x00, 0x00, 0x48, 0x01, 0xD8, 0x48, 0xC7, 0xC1, 0x00, 0x00, 0x00, 0x00, 0x48, 0xC7, 0xC2, 0x00, 0x00, 0x00, 0x00, 0x49, 0xC7, 0xC0, 0x00, 0x00, 0x00, 0x00, 0x49, 0xC7, 0xC1, 0x00, 0x00, 0x00, 0x00, 0xFF, 0xD0, 0x66, 0x81, 0xC4, 0x00, 0x06, 0xC9, 0xC3 };

    printf("[+] filepath is %s\n[+] has offset=%s\n", args->filepath, args->opt & SRDI_CALLOFFSET ? "true" : "false");
